const {App,Command} = require('.')

let app = new App({
    port: 8400, // The port the server should listen to
    commands: [ // Array of available commands
        class MyCommand extends Command {
            initialize() {
                this.name = 'login' // The name of the command
            }
            execute(data) { // The method that will be executed if the command is triggered
                console.log(data, this.client.test)
            }
        }
    ]
});

// Event to define additional attributes and methods for the new client (once the client connects)
app.on('client.connect', (client) => {
    client.test = 'abc';
});

// Event that will be fired if the client disconnects
app.on('client.disconnect', (client) => {

});

app.run();